package NewPackage;

public class CalculQuadrat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int costat;
		costat = 9;
		
		int n = 4;
		int p = n * costat;
		//n=nombre de costats
		//p=perimetre
		System.out.print("El perímetre és: ");
		System.out.println(p);
		
		int a = costat * costat;
		//a=àrea
		System.out.print("L'àrea és: ");
		System.out.println(a);

	}

}
